package Day13;
//Example 2: Rectangle Dimensions
class Rectangle {
    int length, width;

    Rectangle() {
        this(1, 1);
    }

    Rectangle(int l) {
        this(l, 1);
    }

    Rectangle(int l, int w) {
        length = l;
        width = w;
    }
}

