package Day13;
//Example 3: Course Enrollment
class Course {
    String courseName;
    int duration;

    Course() {
        courseName = "Java";
        duration = 30;
    }

    Course(String name) {
        courseName = name;
        duration = 30;
    }

    Course(String name, int days) {
        courseName = name;
        duration = days;
    }
}

